
var http = require('http');
var url = require('url');
var records = { "records":[
  {"fname":"Jorge","lname":"Fuentes","age":22},
  {"fname":"Daniel","lname":"Fuentes","age":17},
  {"fname":"Elon","lname":"Musk","age":32},
  {"fname":"Henry","lname":"Ford","age":76}
  ]
};

http.createServer(function (req, res) {

  var q = url.parse(req.url, true).query;


  for(var i=0; i<records.records.length; i++) {
      if (records.records[i].fname == q.fname && records.records[i].lname == q.lname) {
              console.log(records.records[i]);
      }
  }


  res.writeHead(200, {'Content-Type': 'text/html'});
  res.write('Hello ' + q.fname + ' ' + q.lname);
  return res.end();
}).listen(8080);

console.log('listening');
